import type { ToastMessageOptions } from "primevue/toast";
import { useToast } from "primevue/usetoast";

import { DEFAULT_VUE_PRIME_TOAST_OPTIONS } from "~/composables/vue-prime/constants/vue-prime.constants";

type UseVuePrimeToasts = {
  addToast: (options: ToastMessageOptions) => void;
  addSuccessToast: (options: ToastMessageOptions) => void;
  addInfoToast: (options: ToastMessageOptions) => void;
  addWarnToast: (options: ToastMessageOptions) => void;
  addErrorToast: (options: ToastMessageOptions) => void;
};

function useVuePrimeToasts(): UseVuePrimeToasts {
  const toast = useToast();

  function addToast(options: ToastMessageOptions): void {
    toast.add({
      ...DEFAULT_VUE_PRIME_TOAST_OPTIONS,
      ...options,
    });
  }

  function addSuccessToast(options: ToastMessageOptions): void {
    addToast({ severity: "success", ...options });
  }

  function addInfoToast(options: ToastMessageOptions): void {
    addToast({ severity: "info", ...options });
  }

  function addWarnToast(options: ToastMessageOptions): void {
    addToast({ severity: "warn", ...options });
  }

  function addErrorToast(options: ToastMessageOptions): void {
    addToast({ severity: "error", ...options });
  }
  return {
    addToast,
    addSuccessToast,
    addInfoToast,
    addWarnToast,
    addErrorToast,
  };
}

export { useVuePrimeToasts };

import type { FetchResponse } from "ofetch";

import type { WerewolvesAssistantApiError } from "~/composables/api/error/types/api-error.types";
import { useVuePrimeToasts } from "~/composables/vue-prime/useVuePrimeToasts";

type UseWerewolvesAssistantApiError = {
  handleWerewolvesAssistantApiError: (error: { response: FetchResponse<WerewolvesAssistantApiError> }) => void;
};

function useWerewolvesAssistantApiError(): UseWerewolvesAssistantApiError {
  const { addErrorToast } = useVuePrimeToasts();
  const { t } = useI18n();

  function handleWerewolvesAssistantApiError({ response }: { response: FetchResponse<WerewolvesAssistantApiError> }): void {
    const { _data: errorData } = response;
    // eslint-disable-next-line no-console
    console.error(errorData);
    addErrorToast({
      summary: t(`composables.useWerewolvesAssistantApiError.statusCode.${response.status}`),
      detail: t(`composables.useWerewolvesAssistantApiError.checkConsoleForMoreDetails`),
    });
  }
  return { handleWerewolvesAssistantApiError };
}

export { useWerewolvesAssistantApiError };

function removeTrailingSlashes(url: string): string {
  return url.replace(/\/+$/u, "");
}

export { removeTrailingSlashes };

import { createFetch } from "ofetch";

import { useWerewolvesAssistantApiError } from "~/composables/api/error/useWerewolvesAssistantApiError";
import { removeTrailingSlashes } from "~/utils/url.utils";

type UseWerewolvesAssistantApi = {
  fetchWerewolvesAssistantApi: typeof $fetch;
};

function useWerewolvesAssistantApi(): UseWerewolvesAssistantApi {
  const config = useRuntimeConfig();
  const { baseUrl } = config.public.werewolvesAssistantApi;
  const { handleWerewolvesAssistantApiError } = useWerewolvesAssistantApiError();

  const fetchWerewolvesAssistantApi = createFetch().create({
    baseURL: removeTrailingSlashes(baseUrl),
    headers: { "Content-Type": "application/json" },
    onResponseError: handleWerewolvesAssistantApiError,
  });

  return { fetchWerewolvesAssistantApi };
}

export { useWerewolvesAssistantApi };

import type { Role } from "~/composables/api/role/types/role.class";
import { useWerewolvesAssistantApi } from "~/composables/api/useWerewolvesAssistantApi";

type UseFetchRoles = {
  fetchRoles: () => Promise<ReturnType<typeof $fetch<Role[]>> | null>;
};

function useFetchRoles(): UseFetchRoles {
  const { fetchWerewolvesAssistantApi } = useWerewolvesAssistantApi();

  async function fetchRoles(): Promise<ReturnType<typeof $fetch<Role[]>> | null> {
    try {
      return await fetchWerewolvesAssistantApi<Role[]>(`/roles`, { method: "GET" });
    } catch (error) {
      return null;
    }
  }
  return { fetchRoles };
}

export { useFetchRoles };

enum StoreIds {
  ROLES = "roles",
  CREATE_GAME_DTO = "createGameDto",
  MAKE_GAME_PLAY_DTO = "makeGamePlayDto",
  GAME = "game",
}

export { StoreIds };

import type { AsyncDataRequestStatus } from "#app/composables/asyncData";
import { defineStore } from "pinia";
import { ref } from "vue";

import type { Role } from "~/composables/api/role/types/role.class";
import { useFetchRoles } from "~/composables/api/role/useFetchRoles";
import { StoreIds } from "~/stores/enums/store.enum";

const useRolesStore = defineStore(StoreIds.ROLES, () => {
  const { fetchRoles } = useFetchRoles();

  const fetchingRoleStatus = ref<AsyncDataRequestStatus>("idle");
  const roles = ref<Role[] | null>(null);

  async function fetchAndSetRoles(): Promise<void> {
    fetchingRoleStatus.value = "pending";
    roles.value = await fetchRoles();
    fetchingRoleStatus.value = "success";
  }
  return {
    roles,
    fetchingRoleStatus,
    fetchAndSetRoles,
  };
});

export { useRolesStore };

import type { ComputedRef, Ref } from "vue";

import { MIN_PLAYERS_IN_GAME } from "~/composables/api/game/constants/game.constants";
import type { CreateGameDto } from "~/composables/api/game/dto/create-game/create-game.dto";
import { RoleNames, RoleSides } from "~/composables/api/role/enums/role.enums";

type UseCreateGameDtoValidation = {
  isMinimumPlayersReached: ComputedRef<boolean>;
  areAllRolesSet: ComputedRef<boolean>;
  doesContainOneVillagerSidedRole: ComputedRef<boolean>;
  doesContainOneWerewolfSidedRole: ComputedRef<boolean>;
  areAdditionalCardsSetForThiefIfPresent: ComputedRef<boolean>;
  areAdditionalCardsSetForActorIfPresent: ComputedRef<boolean>;
  arePlayerGroupsSetForPrejudicedManipulatorIfPresent: ComputedRef<boolean>;
  canCreateGame: ComputedRef<boolean>;
  gameCreationValidationErrors: ComputedRef<string[]>;
};

function useCreateGameDtoValidation(createGameDto: Ref<CreateGameDto>): UseCreateGameDtoValidation {
  const { t } = useI18n();

  const isMinimumPlayersReached = computed<boolean>(() => createGameDto.value.players.length >= MIN_PLAYERS_IN_GAME);

  const areAllRolesSet = computed<boolean>(() => createGameDto.value.players.every(player => player.role.name !== undefined));

  const doesContainOneVillagerSidedRole = computed<boolean>(() => createGameDto.value.players.some(player => player.side.current === RoleSides.VILLAGERS));

  const doesContainOneWerewolfSidedRole = computed<boolean>(() => createGameDto.value.players.some(player => player.side.current === RoleSides.WEREWOLVES));

  const areAdditionalCardsSetForThiefIfPresent = computed<boolean>(() => {
    const isThiefPresent = createGameDto.value.players.some(player => player.role.name === RoleNames.THIEF);
    const areAdditionalCardsSetForThief = createGameDto.value.additionalCards?.some(card => card.recipient === RoleNames.THIEF) === true;
    if (isThiefPresent) {
      return areAdditionalCardsSetForThief;
    }
    return true;
  });

  const areAdditionalCardsSetForActorIfPresent = computed<boolean>(() => {
    const isActorPresent = createGameDto.value.players.some(player => player.role.name === RoleNames.ACTOR);
    const areAdditionalCardsSetForActor = createGameDto.value.additionalCards?.some(card => card.recipient === RoleNames.ACTOR) === true;
    if (isActorPresent) {
      return areAdditionalCardsSetForActor;
    }
    return true;
  });

  const arePlayerGroupsSetForPrejudicedManipulatorIfPresent = computed<boolean>(() => {
    const isPrejudicedManipulatorPresent = createGameDto.value.players.some(player => player.role.name === RoleNames.PREJUDICED_MANIPULATOR);
    const arePlayerGroupsSet = createGameDto.value.players.every(player => player.group);
    if (isPrejudicedManipulatorPresent) {
      return arePlayerGroupsSet;
    }
    return true;
  });

  const canCreateGame = computed<boolean>(() => isMinimumPlayersReached.value &&
    areAllRolesSet.value &&
    doesContainOneVillagerSidedRole.value &&
    doesContainOneWerewolfSidedRole.value &&
    areAdditionalCardsSetForThiefIfPresent.value &&
    areAdditionalCardsSetForActorIfPresent.value &&
    arePlayerGroupsSetForPrejudicedManipulatorIfPresent.value);

  const gameCreationValidationErrors = computed<string[]>(() => {
    const errors: Record<string, boolean> = {
      [t("composables.useCreateGameDtoValidation.minimumOfPlayersNotReached")]: !isMinimumPlayersReached.value,
      [t("composables.useCreateGameDtoValidation.notAllRolesAreSet")]: !areAllRolesSet.value,
      [t("composables.useCreateGameDtoValidation.noVillagerSidedRole")]: !doesContainOneVillagerSidedRole.value,
      [t("composables.useCreateGameDtoValidation.noWerewolfSidedRole")]: !doesContainOneWerewolfSidedRole.value,
      [t("composables.useCreateGameDtoValidation.thiefAdditionalCardsNotSet")]: !areAdditionalCardsSetForThiefIfPresent.value,
      [t("composables.useCreateGameDtoValidation.actorAdditionalCardsNotSet")]: !areAdditionalCardsSetForActorIfPresent.value,
      [t("composables.useCreateGameDtoValidation.prejudicedManipulatorGroupsNotSet")]: !arePlayerGroupsSetForPrejudicedManipulatorIfPresent.value,
    };

    return Object.keys(errors).filter((key: keyof typeof errors) => errors[key]);
  });

  return {
    isMinimumPlayersReached,
    areAllRolesSet,
    doesContainOneVillagerSidedRole,
    doesContainOneWerewolfSidedRole,
    areAdditionalCardsSetForThiefIfPresent,
    areAdditionalCardsSetForActorIfPresent,
    arePlayerGroupsSetForPrejudicedManipulatorIfPresent,
    canCreateGame,
    gameCreationValidationErrors,
  };
}

export { useCreateGameDtoValidation };

import { stringify } from "qs";

import type { CreateGamePlayerDto } from "~/composables/api/game/dto/create-game/create-game-player/create-game-player.dto";
import type { RoleNames } from "~/composables/api/role/enums/role.enums";
import { useWerewolvesAssistantApi } from "~/composables/api/useWerewolvesAssistantApi";

type GetRandomGameCompositionQuery = {
  players: { name: string }[];
  excludedRoles?: RoleNames[];
};

type UseFetchRandomGameComposition = {
  fetchRandomGameComposition: (
    query: GetRandomGameCompositionQuery,
  ) => Promise<ReturnType<typeof $fetch<CreateGamePlayerDto[] | null>>>;
};

function useFetchRandomGameComposition(): UseFetchRandomGameComposition {
  const { fetchWerewolvesAssistantApi } = useWerewolvesAssistantApi();

  async function fetchRandomGameComposition(query: GetRandomGameCompositionQuery): Promise<ReturnType<typeof $fetch<CreateGamePlayerDto[] | null>>> {
    const stringifiedQuery = stringify(query);

    return fetchWerewolvesAssistantApi<CreateGamePlayerDto[] | null>(`/games/random-composition?${stringifiedQuery}`, { method: "GET" });
  }
  return { fetchRandomGameComposition };
}

export { useFetchRandomGameComposition };

