import type { ToastMessageOptions } from "primevue/toast";

const DEFAULT_VUE_PRIME_TOAST_OPTIONS: ToastMessageOptions = {
  life: 4000,
  closable: true,
};

export { DEFAULT_VUE_PRIME_TOAST_OPTIONS };

import type { ToastMessageOptions } from "primevue/toast";
import { useToast } from "primevue/usetoast";

import { DEFAULT_VUE_PRIME_TOAST_OPTIONS } from "~/composables/vue-prime/constants/vue-prime.constants";

type UseVuePrimeToasts = {
  addToast: (options: ToastMessageOptions) => void;
  addSuccessToast: (options: ToastMessageOptions) => void;
  addInfoToast: (options: ToastMessageOptions) => void;
  addWarnToast: (options: ToastMessageOptions) => void;
  addErrorToast: (options: ToastMessageOptions) => void;
};

function useVuePrimeToasts(): UseVuePrimeToasts {
  const toast = useToast();

  function addToast(options: ToastMessageOptions): void {
    toast.add({
      ...DEFAULT_VUE_PRIME_TOAST_OPTIONS,
      ...options,
    });
  }

  function addSuccessToast(options: ToastMessageOptions): void {
    addToast({ severity: "success", ...options });
  }

  function addInfoToast(options: ToastMessageOptions): void {
    addToast({ severity: "info", ...options });
  }

  function addWarnToast(options: ToastMessageOptions): void {
    addToast({ severity: "warn", ...options });
  }

  function addErrorToast(options: ToastMessageOptions): void {
    addToast({ severity: "error", ...options });
  }
  return {
    addToast,
    addSuccessToast,
    addInfoToast,
    addWarnToast,
    addErrorToast,
  };
}

export { useVuePrimeToasts };

import type { FetchResponse } from "ofetch";

import type { WerewolvesAssistantApiError } from "~/composables/api/error/types/api-error.types";
import { useVuePrimeToasts } from "~/composables/vue-prime/useVuePrimeToasts";

type UseWerewolvesAssistantApiError = {
  handleWerewolvesAssistantApiError: (error: { response: FetchResponse<WerewolvesAssistantApiError> }) => void;
};

function useWerewolvesAssistantApiError(): UseWerewolvesAssistantApiError {
  const { addErrorToast } = useVuePrimeToasts();
  const { t } = useI18n();

  function handleWerewolvesAssistantApiError({ response }: { response: FetchResponse<WerewolvesAssistantApiError> }): void {
    const { _data: errorData } = response;
    // eslint-disable-next-line no-console
    console.error(errorData);
    addErrorToast({
      summary: t(`composables.useWerewolvesAssistantApiError.statusCode.${response.status}`),
      detail: t(`composables.useWerewolvesAssistantApiError.checkConsoleForMoreDetails`),
    });
  }
  return { handleWerewolvesAssistantApiError };
}

export { useWerewolvesAssistantApiError };

function removeTrailingSlashes(url: string): string {
  return url.replace(/\/+$/u, "");
}

export { removeTrailingSlashes };

import { createFetch } from "ofetch";

import { useWerewolvesAssistantApiError } from "~/composables/api/error/useWerewolvesAssistantApiError";
import { removeTrailingSlashes } from "~/utils/url.utils";

type UseWerewolvesAssistantApi = {
  fetchWerewolvesAssistantApi: typeof $fetch;
};

function useWerewolvesAssistantApi(): UseWerewolvesAssistantApi {
  const config = useRuntimeConfig();
  const { baseUrl } = config.public.werewolvesAssistantApi;
  const { handleWerewolvesAssistantApiError } = useWerewolvesAssistantApiError();

  const fetchWerewolvesAssistantApi = createFetch().create({
    baseURL: removeTrailingSlashes(baseUrl),
    headers: { "Content-Type": "application/json" },
    onResponseError: handleWerewolvesAssistantApiError,
  });

  return { fetchWerewolvesAssistantApi };
}

export { useWerewolvesAssistantApi };

import type { Role } from "~/composables/api/role/types/role.class";
import { useWerewolvesAssistantApi } from "~/composables/api/useWerewolvesAssistantApi";

type UseFetchRoles = {
  fetchRoles: () => Promise<ReturnType<typeof $fetch<Role[]>> | null>;
};

function useFetchRoles(): UseFetchRoles {
  const { fetchWerewolvesAssistantApi } = useWerewolvesAssistantApi();

  async function fetchRoles(): Promise<ReturnType<typeof $fetch<Role[]>> | null> {
    try {
      return await fetchWerewolvesAssistantApi<Role[]>(`/roles`, { method: "GET" });
    } catch (error) {
      return null;
    }
  }
  return { fetchRoles };
}

export { useFetchRoles };

enum StoreIds {
  ROLES = "roles",
  CREATE_GAME_DTO = "createGameDto",
  MAKE_GAME_PLAY_DTO = "makeGamePlayDto",
  GAME = "game",
  GAME_HISTORY_RECORDS = "gameHistoryRecords",
  GAME_EVENTS = "gameEvents",
}

export { StoreIds };

import type { AsyncDataRequestStatus } from "#app/composables/asyncData";
import { defineStore } from "pinia";
import { ref } from "vue";

import type { Role } from "~/composables/api/role/types/role.class";
import type { RoleName } from "~/composables/api/role/types/role.types";
import { useFetchRoles } from "~/composables/api/role/useFetchRoles";
import { StoreIds } from "~/stores/enums/store.enum";

const useRolesStore = defineStore(StoreIds.ROLES, () => {
  const { fetchRoles } = useFetchRoles();

  const fetchingRoleStatus = ref<AsyncDataRequestStatus>("idle");
  const roles = ref<Role[] | null>(null);

  async function fetchAndSetRoles(): Promise<void> {
    fetchingRoleStatus.value = "pending";
    roles.value = await fetchRoles();
    fetchingRoleStatus.value = "success";
  }

  function getRoleWithNameInRoles(roleName: RoleName): Role | undefined {
    return roles.value?.find(role => role.name === roleName);
  }
  return {
    roles,
    fetchingRoleStatus,
    fetchAndSetRoles,
    getRoleWithNameInRoles,
  };
});

export { useRolesStore };

import type { ClassTransformOptions } from "class-transformer";

const DEFAULT_PLAIN_TO_INSTANCE_OPTIONS: Readonly<ClassTransformOptions> = {
  excludeExtraneousValues: true,
  exposeDefaultValues: true,
};

export { DEFAULT_PLAIN_TO_INSTANCE_OPTIONS };

import { Expose, plainToInstance } from "class-transformer";

import type { WitchPotion } from "~/composables/api/game/types/game-play/game-play.types";
import { DEFAULT_PLAIN_TO_INSTANCE_OPTIONS } from "~/utils/constants/class-transformer.constants";

class MakeGamePlayTargetDto {
  @Expose()
  public playerId: string;

  @Expose()
  public drankPotion?: WitchPotion;

  public static create(makeGamePlayTargetDto: MakeGamePlayTargetDto): MakeGamePlayTargetDto {
    return plainToInstance(MakeGamePlayTargetDto, makeGamePlayTargetDto, DEFAULT_PLAIN_TO_INSTANCE_OPTIONS);
  }
}

export { MakeGamePlayTargetDto };

import { Expose, plainToInstance } from "class-transformer";

import { DEFAULT_PLAIN_TO_INSTANCE_OPTIONS } from "~/utils/constants/class-transformer.constants";

class MakeGamePlayVoteDto {
  @Expose()
  public sourceId: string;

  @Expose()
  public targetId: string;

  public static create(makeGamePlayVoteDto: MakeGamePlayVoteDto): MakeGamePlayVoteDto {
    return plainToInstance(MakeGamePlayVoteDto, makeGamePlayVoteDto, DEFAULT_PLAIN_TO_INSTANCE_OPTIONS);
  }
}

export { MakeGamePlayVoteDto };

import { Expose, plainToInstance, Type } from "class-transformer";

import { MakeGamePlayTargetDto } from "~/composables/api/game/dto/make-game-play/make-game-play-target/make-game-play-target.dto";
import { MakeGamePlayVoteDto } from "~/composables/api/game/dto/make-game-play/make-game-play-vote/make-game-play-vote.dto";
import type { RoleSide } from "~/composables/api/role/types/role.types";
import { DEFAULT_PLAIN_TO_INSTANCE_OPTIONS } from "~/utils/constants/class-transformer.constants";

class MakeGamePlayDto {
  @Type(() => MakeGamePlayTargetDto)
  @Expose()
  public targets?: MakeGamePlayTargetDto[];

  @Type(() => MakeGamePlayVoteDto)
  @Expose()
  public votes?: MakeGamePlayVoteDto[];

  @Expose()
  public doesJudgeRequestAnotherVote?: boolean;

  @Expose()
  public chosenCardId?: string;

  @Expose()
  public chosenSide?: RoleSide;

  public static create(makeGamePlayDto: MakeGamePlayDto): MakeGamePlayDto {
    return plainToInstance(MakeGamePlayDto, makeGamePlayDto, DEFAULT_PLAIN_TO_INSTANCE_OPTIONS);
  }
}

export { MakeGamePlayDto };

import { defineStore } from "pinia";

import { MakeGamePlayTargetDto } from "~/composables/api/game/dto/make-game-play/make-game-play-target/make-game-play-target.dto";
import { MakeGamePlayVoteDto } from "~/composables/api/game/dto/make-game-play/make-game-play-vote/make-game-play-vote.dto";
import { MakeGamePlayDto } from "~/composables/api/game/dto/make-game-play/make-game-play.dto";
import type { WitchPotion } from "~/composables/api/game/types/game-play/game-play.types";
import type { RoleSide } from "~/composables/api/role/types/role.types";
import { StoreIds } from "~/stores/enums/store.enum";

const useMakeGamePlayDtoStore = defineStore(StoreIds.MAKE_GAME_PLAY_DTO, () => {
  const makeGamePlayDto = ref<MakeGamePlayDto>(MakeGamePlayDto.create({}));

  function setMakeGamePlayDto(makeGamePlayDtoValue: MakeGamePlayDto): void {
    makeGamePlayDto.value = MakeGamePlayDto.create(makeGamePlayDtoValue);
  }

  function resetMakeGamePlayDto(): void {
    makeGamePlayDto.value = MakeGamePlayDto.create({});
  }

  function addMakeGamePlayTargetDto(target: MakeGamePlayTargetDto): void {
    if (!makeGamePlayDto.value.targets) {
      makeGamePlayDto.value.targets = [];
    }
    makeGamePlayDto.value.targets.push(MakeGamePlayTargetDto.create(target));
  }

  function removeMakeGamePlayTargetDto(targetId: string): void {
    if (!makeGamePlayDto.value.targets) {
      return;
    }
    const targetIndex = makeGamePlayDto.value.targets.findIndex(target => target.playerId === targetId);
    if (targetIndex !== -1) {
      makeGamePlayDto.value.targets = makeGamePlayDto.value.targets.toSpliced(targetIndex, 1);
    }
    if (makeGamePlayDto.value.targets.length === 0) {
      makeGamePlayDto.value.targets = undefined;
    }
  }

  function removeFirstMakeGamePlayTargetDto(): void {
    if (!makeGamePlayDto.value.targets || makeGamePlayDto.value.targets.length === 0) {
      return;
    }
    const firstTarget = makeGamePlayDto.value.targets[0];
    removeMakeGamePlayTargetDto(firstTarget.playerId);
  }

  function removeFirstMakeGamePlayTargetDtoWithPotion(drankPotion: WitchPotion): void {
    const firstDrankPotionTarget = makeGamePlayDto.value.targets?.find(target => target.drankPotion === drankPotion);
    if (firstDrankPotionTarget) {
      removeMakeGamePlayTargetDto(firstDrankPotionTarget.playerId);
    }
  }

  function addMakeGamePlayVoteDto(vote: MakeGamePlayVoteDto): void {
    if (!makeGamePlayDto.value.votes) {
      makeGamePlayDto.value.votes = [];
    }
    makeGamePlayDto.value.votes.push(MakeGamePlayVoteDto.create(vote));
  }

  function removeMakeGamePlayVoteDto(sourceId: string): void {
    if (!makeGamePlayDto.value.votes) {
      return;
    }
    const voteIndex = makeGamePlayDto.value.votes.findIndex(vote => vote.sourceId === sourceId);
    if (voteIndex !== -1) {
      makeGamePlayDto.value.votes = makeGamePlayDto.value.votes.toSpliced(voteIndex, 1);
    }
    if (makeGamePlayDto.value.votes.length === 0) {
      makeGamePlayDto.value.votes = undefined;
    }
  }

  function setDoesJudgeRequestAnotherVote(doesJudgeRequestAnotherVote: boolean | undefined): void {
    makeGamePlayDto.value.doesJudgeRequestAnotherVote = doesJudgeRequestAnotherVote;
  }

  function setChosenCardId(chosenCardId: string | undefined): void {
    makeGamePlayDto.value.chosenCardId = chosenCardId;
  }

  function setChosenSide(chosenSide: RoleSide | undefined): void {
    makeGamePlayDto.value.chosenSide = chosenSide;
  }
  return {
    makeGamePlayDto,
    setMakeGamePlayDto,
    resetMakeGamePlayDto,
    addMakeGamePlayTargetDto,
    removeMakeGamePlayTargetDto,
    removeFirstMakeGamePlayTargetDto,
    removeFirstMakeGamePlayTargetDtoWithPotion,
    addMakeGamePlayVoteDto,
    removeMakeGamePlayVoteDto,
    setDoesJudgeRequestAnotherVote,
    setChosenCardId,
    setChosenSide,
  };
});

export { useMakeGamePlayDtoStore };
