import type { GamePhase } from "~/composables/api/game/types/game.types";

type UseGamePhase = {
  getGamePhaseWithTurnText: (phase: GamePhase, turn: number) => string;
};

function useGamePhase(): UseGamePhase {
  const { t } = useI18n();

  function getGamePhaseWithTurnText(phase: GamePhase, turn: number): string {
    const phaseText = t(`shared.game.phase.${phase}`);

    return `${phaseText} ${turn}`;
  }
  return { getGamePhaseWithTurnText };
}

export { useGamePhase };

import type { GameHistoryRecord } from "~/composables/api/game/types/game-history-record/game-history-record.class";
import { useWerewolvesAssistantApi } from "~/composables/api/useWerewolvesAssistantApi";

type UseFetchGameHistory = {
  getGameHistoryRecords: (gameId: string) => Promise<ReturnType<typeof $fetch<GameHistoryRecord[]>> | null>;
};

function useFetchGameHistoryRecords(): UseFetchGameHistory {
  const { fetchWerewolvesAssistantApi } = useWerewolvesAssistantApi();

  async function getGameHistoryRecords(gameId: string): Promise<ReturnType<typeof $fetch<GameHistoryRecord[]>> | null> {
    try {
      return await fetchWerewolvesAssistantApi<GameHistoryRecord[]>(`/games/${gameId}/history`, { method: "GET" });
    } catch (error) {
      return null;
    }
  }
  return { getGameHistoryRecords };
}

export { useFetchGameHistoryRecords };

import type { AsyncDataRequestStatus } from "#app/composables/asyncData";
import { defineStore } from "pinia";

import { useFetchGameHistoryRecords } from "~/composables/api/game/game-history-record/useFetchGameHistoryRecords";
import type { GameHistoryRecord } from "~/composables/api/game/types/game-history-record/game-history-record.class";
import { StoreIds } from "~/stores/enums/store.enum";

const useGameHistoryRecordsStore = defineStore(StoreIds.GAME_HISTORY_RECORDS, () => {
  const { getGameHistoryRecords } = useFetchGameHistoryRecords();

  const gameHistoryRecords = ref<GameHistoryRecord[]>([]);
  const fetchingGameHistoryRecordsStatus = ref<AsyncDataRequestStatus>("idle");

  async function fetchAndSetGameHistoryRecords(gameId: string): Promise<void> {
    fetchingGameHistoryRecordsStatus.value = "pending";
    const fetchedGameHistoryRecords = await getGameHistoryRecords(gameId);
    if (!fetchedGameHistoryRecords) {
      fetchingGameHistoryRecordsStatus.value = "error";

      return;
    }
    gameHistoryRecords.value = fetchedGameHistoryRecords;
    fetchingGameHistoryRecordsStatus.value = "success";
  }
  return {
    gameHistoryRecords,
    fetchingGameHistoryRecordsStatus,
    fetchAndSetGameHistoryRecords,
  };
});

export { useGameHistoryRecordsStore };
