  let wrapper: ReturnType<typeof mount<typeof AboutAvailableRoles>>;

  beforeEach(async() => {
    vi.spyOn(UseRoleName, "useRoleName").mockReturnValue({ getRoleNameLabel: vi.fn((roleName: RoleNames) => roleName) });
